import React, { useState } from "react";
import TextInputField from "../../component/TextInput";
import MainButton from "../../component/Button";
import "./list.css";
import ListItem from "../../component/ListItem";

const TodoAddScreen = () => {
  const [inputType, setInputType] = useState("");
  const [items, setItems] = useState([]);

  // Add item to list
  const handleSubmit = (e) => {
    setItems((prevItems) => {
      return [...prevItems, { task: inputType }];
    })
    setInputType(""); // Clear the input field after adding the task
  };

  console.log(items);

  return (
    <div className="root">
      <div className="main-container">
        <div className="container">
          <h1 className="heading"> Todo-List </h1>

          <div className="input-container">
            <TextInputField
              type="text"
              placeholder="Please Enter Your Task"
              value={inputType}
              onChange={(e) => setInputType(e.target.value)}
            />

            <MainButton
              title="Add Task"
              onClick={handleSubmit} // Just pass the function reference, don't call it here
            />
          </div>

          <div>
            <ul>
              {items.map((todoItem, index) => (
                <ListItem key={index} task={todoItem} />
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TodoAddScreen;
